func Test_commonChars(t *testing.T) {
	var label string
	for _, tc := range testCases {
		val := tc.Input
		if len(tc.Input) > 20 {
			val = tc.Input[0:10]
		}
		label = fmt.Sprintf("Case: Input: %v, Output: %v\n", val, tc.Output)
		t.Run(label, func(t *testing.T) {
			output := commonChars(tc.Input)
			if !reflect.DeepEqual(output, tc.Output) {
				t.Errorf("Expected output to be %v but we got %v", tc.Output, output)
			}
		})
	}
}

func Benchmark_commmonChars(b *testing.B) {
	for i := 0; i < b.N; i++ {
		_ = commonChars(testCases[0].Input)
	}
}
