package solution2309

import (
	"fmt"
	"testing"
)

var testCases = []struct {
	Input  string
	Output string
}{
	{
		Input:  "lEeTcOdE",
		Output: "E",
	},
	{
		Input:  "arRAzFif",
		Output: "R",
	},
	{
		Input:  "",
		Output: "",
	},
}

func Test_greatestLetter(t *testing.T) {
	var label string
	for _, tc := range testCases {
		label = fmt.Sprintf("Case: Input: %v Output: %v\n", tc.Input, tc.Output)
		t.Run(label, func(t *testing.T) {
			output := greatestLetter(tc.Input)
			if output != tc.Output {
				t.Errorf("Expected output to be %v but we got %v", tc.Output, output)
			}
		})
	}
}

func Benchmark_greatestLetter(b *testing.B) {
	for i := 0; i < b.N; i++ {
		_ = greatestLetter(testCases[0].Input)
	}
}
